# convert an array of values into a dataset matrix
def create_dataset(dataset_univar, look_back):
    dataX, dataY = [], []
    for i in range(len(dataset_univar)-look_back-1):
        a = dataset_univar[i:(i+look_back), 0]
        dataX.append(a)
        dataY.append(dataset_univar[i + look_back, 0])
    return np.array(dataX), np.array(dataY)
